mapper.select:
select bno, title, content, writer, writerDate, hit
from(
	select rownum rnum, bno, title, content, writer, writerDate, hit
	from (
			select bno, title, content, writer, writerDate, hit
			from board
			 
			order by bno
		)
)	
where rnum between #{startRow} and #{endRow}

select bno, title, content, writer, writerDate, hit
from board
where bno=#{bno}

mapper.update:
update board
set hit = hit +1
where bno = #{bno}

update board
set title = #{title}, content = #{content}, writer = #{writer}
where bno = #{bno}
