mapper.select:
select bno, tittle, content, date3, hit
from(
	select rownum rnum, bno, tittle, content, date3, hit
	from (
			select bno, tittle, content, date3, hit
			from notice
		)
)	
where rnum between 1  and 10

select bno, tittle, content, date3, hit
from notice
where bno=#{bno}

mapper.update:
update notice
set hit = hit +1
where bno = #{bno}

mapper.select:
	
      select content
      from notice 
       where bno=#{bno}
