mapper.select:
select bno, tittle, url, date3, hit
from(
	select rownum rnum, bno, tittle, url, date3, hit
	from (
			select bno, tittle, url, date3, hit
			from notice
			 
		)
)	
where rnum between 1  and 10

select bno, tittle, url, date3, hit
from notice
where bno=#{bno}

mapper.update:
update notice
set hit = hit +1
where bno = #{bno}

mapper.select:
SELECT url
FROM notice
WHERE bno = #{bno}
